var table = $('#myTable').DataTable();

$('#submit').on( 'click', function () {
	$.getJSON( 'newTable', null, function ( json ) {
		table.destroy();
		$('#myTable').empty(); // empty in case the columns change

		table = $('#myTable').DataTable( {
			columns: json.columns,
			data:    json.rows
		} );
	} );
} );
