var table = $('#example').DataTable( {
	ajax: "/data",
	serverSide: true
} );

table.on( 'xhr', function ( e, settings, json ) {
	table.off( 'xhr' );
	console.log( 'Ajax event occurred. Returned data: ', json );
} );

// note that this is the same effect as using `table.one(...);`
