var table = $('#example').DataTable();
var order = table.order();

alert( 'Column '+order[0][0]+' is the ordering column' );

var table = $('#example').DataTable();

// Sort by column 1 and then re-draw
table
	.order( [ 1, 'asc' ] )
	.draw();

var table = $('#example').DataTable();

// Sort by columns 1 and 2 and redraw
table
	.order( [ 1, 'asc' ], [ 2, 'asc' ] )
	.draw();

var table = $('#example').DataTable();

// Sort by columns 1 and 2 and redraw
table
	.order( [[ 1, 'asc' ], [ 2, 'asc' ]] )
	.draw();
