$(document).ready( function() {
  initTable();
  tableActions();
} );

function initTable () {
  return $('#example').dataTable( {
    "scrollY": "200px",
    "paginate": false,
    "retrieve": true
  } );
}

function tableActions () {
  var table = initTable();

  // perform API operations with `table`
  // ...
}
